import React from 'react';
import './DonationSection.css';
import cowImage from '../assets/images/cow-feeding.jpg';

interface DonationCardProps {
  title: string;
  amount: number;
  image: string;
}

const DonationCard: React.FC<DonationCardProps> = ({ title, amount, image }) => {
  return (
    <div className="donation-card">
      <img src={image} alt={title} className="donation-image" />
      <h3 className="donation-title">{title}</h3>
      <div className="donation-footer">
        <span className="donation-amount">â‚¹ {amount.toLocaleString()}</span>
        <button className="add-donation-btn">Add Donation</button>
      </div>
    </div>
  );
};

const DonationSection = () => {
  const feedCowsData = [
    { title: 'Feed 10 Cows', amount: 1500, image: cowImage },
    { title: 'Feed 20 Cows', amount: 3000, image: cowImage },
    { title: 'Feed 50 Cows', amount: 7000, image: cowImage },
    { title: '1 Day Maintenance of Cows', amount: 51000, image: cowImage },
  ];

  const adoptCowsData = [
    { title: 'Adopt 1 Cow for 1 Month', amount: 3000, image: cowImage },
    { title: 'Adopt 1 Cow for 3 Month', amount: 9000, image: cowImage },
    { title: 'Adopt 1 Cow for 1 Year', amount: 36000, image: cowImage },
    { title: 'Adopt 1 Cow for Its Lifetime', amount: 360000, image: cowImage },
  ];

  return (
    <div className="donation-section">
      <section className="feed-cows">
        <h2 className="section-title">Feed Cows</h2>
        <div className="donation-grid">
          {feedCowsData.map((item, index) => (
            <DonationCard key={index} {...item} />
          ))}
        </div>
      </section>

      <section className="adopt-cows">
        <h2 className="section-title">Adopt A Cow</h2>
        <div className="donation-grid">
          {adoptCowsData.map((item, index) => (
            <DonationCard key={index} {...item} />
          ))}
        </div>
      </section>
    </div>
  );
};

export default DonationSection;
