export const scrollConfig = {
  // Time settings
  scrollTimePerTile: 2000, // Time in milliseconds to scroll one tile (e.g., 3000ms = 3 seconds)

  // Layout settings
  tilesPerView: 4,
  gapBetweenTiles: 20, // in pixels

  // Scroll behavior
  scrollBehavior: 'smooth' as const
};
