import React from 'react';
import './EventGallery.css';
import eventImage from '../assets/images/event-photo.jpg';

interface EventCardProps {
  title: string;
  date: string;
  image: string;
  description: string;
}

interface VideoCardProps {
  title: string;
  thumbnail: string;
  videoUrl: string;
  description: string;
  date: string;
}

const EventCard: React.FC<EventCardProps> = ({ title, date, image, description }) => {
  return (
    <div className="event-card">
      <img src={image} alt={title} className="event-image" />
      <div className="event-content">
        <h3 className="event-title">{title}</h3>
        <p className="event-date">{date}</p>
        <p className="event-description">{description}</p>
      </div>
    </div>
  );
};

const VideoCard: React.FC<VideoCardProps> = ({ title, thumbnail, videoUrl, description, date }) => {
  return (
    <div className="video-card">
      <a href={videoUrl} target="_blank" rel="noopener noreferrer" className="video-link">
        <div className="thumbnail-container">
          <img src={thumbnail} alt={title} className="video-thumbnail" />
          <div className="play-button">
            <i className="fas fa-play"></i>
          </div>
        </div>
        <div className="video-content">
          <h3 className="video-title">{title}</h3>
          <p className="video-date">{date}</p>
          <p className="video-description">{description}</p>
        </div>
      </a>
    </div>
  );
};

const EventGallery = () => {
  const recentEventsData = [
    {
      title: 'Krishna Janmashtami Celebration',
      date: 'August 15, 2023',
      image: eventImage,
      description: 'Grand celebration of Lord Krishna\'s appearance day with abhishek ceremony.'
    },
    {
      title: 'Ratha Yatra Festival',
      date: 'July 1, 2023',
      image: eventImage,
      description: 'Annual chariot festival with cultural performances and prasadam distribution.'
    },
    {
      title: 'Gaura Purnima Festival',
      date: 'March 7, 2023',
      image: eventImage,
      description: 'Celebrating the appearance day of Sri Chaitanya Mahaprabhu.'
    },
    {
      title: 'Radhashtami Celebration',
      date: 'September 23, 2023',
      image: eventImage,
      description: 'Special ceremonies and cultural programs celebrating Srimati Radharani.'
    }
  ];

  const eventVideosData = [
    {
      title: 'Morning Mangal Aarti',
      thumbnail: eventImage,
      videoUrl: 'https://youtube.com/watch?v=example1',
      description: 'Daily morning aarti ceremony with beautiful kirtan.',
      date: 'October 1, 2023'
    },
    {
      title: 'Kirtan Festival Highlights',
      thumbnail: eventImage,
      videoUrl: 'https://youtube.com/watch?v=example2',
      description: 'Highlights from our annual 24-hour kirtan festival.',
      date: 'September 15, 2023'
    },
    {
      title: 'Temple Cultural Program',
      thumbnail: eventImage,
      videoUrl: 'https://youtube.com/watch?v=example3',
      description: 'Traditional dance and music performances by devotees.',
      date: 'September 5, 2023'
    },
    {
      title: 'Special Guest Lecture',
      thumbnail: eventImage,
      videoUrl: 'https://youtube.com/watch?v=example4',
      description: 'Enlightening discourse on Bhagavad Gita by visiting swami.',
      date: 'August 28, 2023'
    }
  ];

  return (
    <div className="gallery-section">
      <section className="recent-events">
        <h2 className="section-title">Recent Events</h2>
        <div className="event-grid">
          {recentEventsData.map((item, index) => (
            <EventCard key={index} {...item} />
          ))}
        </div>
      </section>

      <section className="event-videos">
        <h2 className="section-title">Event Videos</h2>
        <div className="video-grid">
          {eventVideosData.map((item, index) => (
            <VideoCard key={index} {...item} />
          ))}
        </div>
      </section>
    </div>
  );
};

export default EventGallery;
