export const scrollConfig = {
  // Time settings
  scrollTimePerTile: 300, // 3 seconds per tile
  frameRate: 500 / 2, // 60 FPS
  resetDelay: 500, // Increased delay for smoother transition when resetting
  transitionDuration: 300, // Duration for smooth transitions in milliseconds

  // Layout settings
  tilesPerView: 4,
  gapBetweenTiles: 20, // in pixels

  // Scroll behavior
  scrollBehavior: 'smooth' as const,

  // Calculate steps needed for smooth scrolling
  get stepsPerTile() {
    return 1;
  }
};
