export const scrollConfig = {
  // Time settings
  scrollTimePerTile: 2000, // Time in milliseconds to scroll one tile (reduced for smoother scrolling)

  // Layout settings
  tilesPerView: 4,
  gapBetweenTiles: 20, // in pixels

  // Scroll behavior
  scrollBehavior: 'smooth' as const,

  // Animation settings
  transitionDuration: '2s',
  transitionTimingFunction: 'ease-out'
};
