export const scrollConfig = {
  // Time settings
  scrollTimePerTile: 3000, // 3 seconds per tile
  frameRate: 1000 / 60, // 60 FPS
  resetDelay: 50, // Delay before restarting scroll after reset

  // Layout settings
  tilesPerView: 4,
  gapBetweenTiles: 20, // in pixels

  // Scroll behavior
  scrollBehavior: 'auto' as const,

  // Calculate steps needed for smooth scrolling
  get stepsPerTile() {
    return 2;
  }
};
