import React, { useRef, useState, useEffect } from 'react';
import './EventGallery.css';
import { upcomingFestival, recentEvents, eventVideos, FestivalEvent, EventVideo } from '../data/festivalData';

interface EventCardProps {
  title: string;
  date: string;
  image: string;
  description: string;
}

interface VideoCardProps {
  title: string;
  thumbnail: string;
  videoUrl: string;
  description: string;
  date: string;
}

interface CountdownProps {
  targetDate: string;
  displayDate: string;
}

const EventCard: React.FC<EventCardProps> = ({ title, date, image, description }) => {
  return (
    <div className="event-card">
      <img src={image} alt={title} className="event-image" />
      <div className="event-content">
        <h3 className="event-title">{title}</h3>
        <p className="event-date">{date}</p>
        <p className="event-description">{description}</p>
      </div>
    </div>
  );
};

const VideoCard: React.FC<VideoCardProps> = ({ title, thumbnail, videoUrl, description, date }) => {
  return (
    <div className="video-card">
      <a href={videoUrl} target="_blank" rel="noopener noreferrer" className="video-link">
        <div className="thumbnail-container">
          <img src={thumbnail} alt={title} className="video-thumbnail" />
          <div className="play-button">
            <i className="fas fa-play"></i>
          </div>
        </div>
        <div className="video-content">
          <h3 className="video-title">{title}</h3>
          <p className="video-date">{date}</p>
          <p className="video-description">{description}</p>
        </div>
      </a>
    </div>
  );
};

const Countdown: React.FC<CountdownProps> = ({ targetDate, displayDate }) => {
  const calculateTimeLeft = () => {
    const targetDateTime = new Date(targetDate).getTime();
    const now = new Date().getTime();
    const difference = targetDateTime - now;

    if (difference <= 0) {
      return {
        days: 0,
        hours: 0,
        minutes: 0,
        seconds: 0,
        isExpired: true
      };
    }

    return {
      days: Math.floor(difference / (1000 * 60 * 60 * 24)),
      hours: Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
      minutes: Math.floor((difference % (1000 * 60 * 60)) / (1000 * 60)),
      seconds: Math.floor((difference % (1000 * 60)) / 1000),
      isExpired: false
    };
  };

  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

  useEffect(() => {
    const timer = setInterval(() => {
      const newTimeLeft = calculateTimeLeft();
      setTimeLeft(newTimeLeft);

      if (newTimeLeft.isExpired) {
        clearInterval(timer);
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [targetDate]);

  if (timeLeft.isExpired) {
    return (
      <div className="countdown-timer">
        <div className="countdown-expired">
          Event has started!
        </div>
      </div>
    );
  }

  return (
    <div className="countdown-timer">
      <div className="countdown-item">
        <div className="countdown-value">{String(timeLeft.days).padStart(2, '0')}</div>
        <div className="countdown-label">Days</div>
      </div>
      <div className="countdown-item">
        <div className="countdown-value">{String(timeLeft.hours).padStart(2, '0')}</div>
        <div className="countdown-label">Hours</div>
      </div>
      <div className="countdown-item">
        <div className="countdown-value">{String(timeLeft.minutes).padStart(2, '0')}</div>
        <div className="countdown-label">Minutes</div>
      </div>
      <div className="countdown-item">
        <div className="countdown-value">{String(timeLeft.seconds).padStart(2, '0')}</div>
        <div className="countdown-label">Seconds</div>
      </div>
    </div>
  );
};

const EventGallery = () => {
  const eventGridRef = useRef<HTMLDivElement>(null);
  const videoGridRef = useRef<HTMLDivElement>(null);
  const [isEventHovered, setIsEventHovered] = useState(false);
  const [isVideoHovered, setIsVideoHovered] = useState(false);

  const scroll = (direction: 'prev' | 'next', element: HTMLDivElement | null) => {
    if (element) {
      const scrollAmount = element.clientWidth;
      element.scrollBy({
        left: direction === 'next' ? scrollAmount : -scrollAmount,
        behavior: 'smooth'
      });
    }
  };

  const autoScroll = (element: HTMLDivElement | null) => {
    if (element) {
      const tileWidth = element.clientWidth / 4; // Since we show 4 tiles per view
      const isAtEnd = element.scrollLeft + element.clientWidth >= element.scrollWidth;
      const isAtStart = element.scrollLeft <= 0;

      // We want to move one tile width in 1000ms (1 second)
      // So we'll divide the movement into small steps
      // 60 FPS means we need 60 steps per second
      const scrollStep = tileWidth / 60; // Divide tile width by number of frames

      if (isAtEnd) {
        // Reset to start when reaching the end
        element.scrollTo({ left: 0, behavior: 'auto' });
      } else {
        // Scroll by calculated amount
        element.scrollBy({ left: scrollStep, behavior: 'auto' });
      }
    }
  };

  useEffect(() => {
    let eventInterval: NodeJS.Timeout;
    let videoInterval: NodeJS.Timeout;

    // Running at 60 FPS (1000ms / 60 ≈ 16.67ms)
    const frameRate = 1000 / 60;

    if (!isEventHovered && eventGridRef.current) {
      eventInterval = setInterval(() => {
        autoScroll(eventGridRef.current);
      }, frameRate);
    }

    if (!isVideoHovered && videoGridRef.current) {
      videoInterval = setInterval(() => {
        autoScroll(videoGridRef.current);
      }, frameRate);
    }

    return () => {
      clearInterval(eventInterval);
      clearInterval(videoInterval);
    };
  }, [isEventHovered, isVideoHovered]);

  return (
    <div className="gallery-section">
      <section className="upcoming-festival">
        <div className="festival-banner">
          <img src={upcomingFestival.image} alt={upcomingFestival.title} />
        </div>
        <div className="festival-content-wrapper">
          <div className="festival-content">
            <h2 className="festival-title">{upcomingFestival.title}</h2>
            <p className="festival-date">
              <i className="fas fa-calendar-alt"></i>
              {upcomingFestival.displayDate}
            </p>
            <p className="festival-description">{upcomingFestival.description}</p>
            <Countdown 
              targetDate={upcomingFestival.date} 
              displayDate={upcomingFestival.displayDate}
            />
          </div>
        </div>
      </section>

      <section className="recent-events">
        <h2 className="section-title">Recent Events</h2>
        <div className="scroll-container">
          <button 
            className="scroll-button prev" 
            onClick={() => scroll('prev', eventGridRef.current)}
          >
            <i className="fas fa-chevron-left"></i>
          </button>
          <div 
            className="event-grid" 
            ref={eventGridRef}
            onMouseEnter={() => setIsEventHovered(true)}
            onMouseLeave={() => setIsEventHovered(false)}
          >
            {recentEvents.map((item, index) => (
              <EventCard key={index} {...item} />
            ))}
          </div>
          <button 
            className="scroll-button next" 
            onClick={() => scroll('next', eventGridRef.current)}
          >
            <i className="fas fa-chevron-right"></i>
          </button>
        </div>
      </section>

      <section className="event-videos">
        <h2 className="section-title">Event Videos</h2>
        <div className="scroll-container">
          <button 
            className="scroll-button prev" 
            onClick={() => scroll('prev', videoGridRef.current)}
          >
            <i className="fas fa-chevron-left"></i>
          </button>
          <div 
            className="video-grid" 
            ref={videoGridRef}
            onMouseEnter={() => setIsVideoHovered(true)}
            onMouseLeave={() => setIsVideoHovered(false)}
          >
            {eventVideos.map((item, index) => (
              <VideoCard key={index} {...item} />
            ))}
          </div>
          <button 
            className="scroll-button next" 
            onClick={() => scroll('next', videoGridRef.current)}
          >
            <i className="fas fa-chevron-right"></i>
          </button>
        </div>
      </section>
    </div>
  );
};

export default EventGallery;
