export const scrollConfig = {

  // Layout settings
  tilesPerView: 4,
  gapBetweenTiles: 20, // in pixels

  // Scroll behavior
  scrollBehavior: 'smooth' as const,

  // Calculate steps needed for smooth scrolling
  get stepsPerTile() {
    return 1;
  }
};
